$(function(){
  //切换样式(默认mouseover触发)
  var tab_nav_li = $('.JS_tab_nav li');
  tab_nav_li.mouseover(function(){
    $(this).addClass('selected')
  			.siblings().removeClass('selected');
  var tab_nav_li_index = tab_nav_li.index(this);
  $('.tab_switch .tabcon').eq(tab_nav_li_index).show()
  			.siblings().hide();
  }).hover(function(){
    $(this).addClass('hover');
  },function(){
    $(this).removeClass('hover');	
  });
  //切换加滚动样式，注意必不可少.scoll_box{position:relative}属性，不然IE6、7会出问题
  var scoll_nav_li = $('.JS_scoll_nav li');
  scoll_nav_li.mouseover(function(){
  	$(this).addClass('selected')
  			 .siblings().removeClass('selected');
  var scoll_nav_li_index = scoll_nav_li.index(this);   
  $('.JS_scoll_switch .scoll_box').eq(scoll_nav_li_index).addClass('selected')
  				 .siblings().removeClass('selected');
  });
  $("#JS_promo img").banner_thaw({
		thumbObj: "#JS_promo_num li",
		thumbNowClass: "current",
		botPrev:"#JS_promo span.prev",
		botNext:"#JS_promo span.next",
		changeTime: 3000
	});
	
  $(".tabcon1 .scoll").jCarouselLite({//滚动控制
  	btnNext: ".tabcon1 .next",
  	btnPrev: ".tabcon1 .prev",
  	visible: 6,
  	scroll: 1,
  	auto: 1200,
  	speed: 500
  });
	
	$(".tabcon2 .scoll").jCarouselLite({
  	btnNext: ".tabcon2 .next",
  	btnPrev: ".tabcon2 .prev",
  	visible: 6,
  	scroll: 1,
  	auto: 1200,
  	speed: 500
  });
	
	$(".tabcon3 .scoll").jCarouselLite({
  	btnNext: ".tabcon3 .next",
  	btnPrev: ".tabcon3 .prev",
  	visible: 6,
  	scroll: 1,
  	auto: 1200,
  	speed: 500
  });
	
	$(".tabcon4 .scoll").jCarouselLite({
  	btnNext: ".tabcon4 .next",
  	btnPrev: ".tabcon4 .prev",
  	visible: 6,
  	scroll: 1,
  	auto: 1200,
  	speed: 500
  });
	
	function checkSearchForm(){
        if(document.getElementById('keyword').value){
            return true;
        }else{
            alert("请输入搜索关键词！");
            return false;
  }};
	
	$('.promo .arrow').mousedown(
	function(){
		$(this).addClass('hover')
	}).mouseout(
	function(){
		$(this).removeClass('hover')
	})
	
	/*展开收起*/
  $(".list_con li samp").toggle(
	  function(){
	    $(this).addClass("hide_detail").html("关闭").parent().children(".detail").slideDown();
		  //$(this);
		},function(){
	  $(this).removeClass("hide_detail").html("价格").parent().children(".detail").slideUp();
		//$(this);
	});

  $('.sale_select i').toggle(
	function(){
	    $(this).addClass("selected");
			var b_selected= $(this).parent().children("b.selected").html();
			if(b_selected=="无"){b_selected=""};
			$(this).parent().children("b.selected").html(b_selected + $(this).html().replace("<b></b>"," "))
		},function(){
	  $(this).removeClass("selected");
		  var b_selected= $(this).parent().children("b.selected").html();
			$(this).parent().children("b.selected").html(b_selected.replace($(this).html().replace("<b></b>"," "),""));
	});
});
